package iscteiul.ista.battleship;

import java.util.List;

public interface IGame {
    IShip fire(IPosition pos);

    List<IPosition> getShots();

    int getRepeatedShots();

    int getInvalidShots();

    int getHits();

    int getSunkShips();

    int getRemainingShips();

    void printValidShots();

    void printFleet();
}

/**
 * Game API for firing at positions and querying shot statistics.
 */
package iscteiul.ista.battleship;

import java.util.List;

public interface IGame {
    /**
     * Fire a shot at the given board position.
     *
     * @param pos the target position to fire at
     * @return the ship hit at that position, or null if the shot missed or sank nothing
     */
    IShip fire(IPosition pos);

    /**
     * Returns the list of all positions that have been fired upon.
     *
     * @return list of fired positions (in chronological order)
     */
    List<IPosition> getShots();

    /**
     * Number of shots that repeated a previously fired position.
     *
     * @return repeated shot count
     */
    int getRepeatedShots();

    /**
     * Number of invalid shots (e.g., out of bounds).
     *
     * @return invalid shot count
     */
    int getInvalidShots();

    /**
     * Number of successful hits recorded.
     *
     * @return hit count
     */
    int getHits();

    /**
     * Number of ships that have been sunk.
     *
     * @return sunk ships count
     */
    int getSunkShips();

    /**
     * Number of ships still floating (not sunk).
     *
     * @return remaining ship count
     */
    int getRemainingShips();

    /**
     * Print a board view showing valid shots.
     */
    void printValidShots();

    /**
     * Print a board view showing the fleet positions.
     */
    void printFleet();
}

/**
 *
 */
package iscteiul.ista.battleship;

/**
 * @author fba
 */
public interface IPosition {
    int getRow();

    int getColumn();

    boolean equals(Object other);

    boolean isAdjacentTo(IPosition other);

    void occupy();

    void shoot();

    boolean isOccupied();

    boolean isHit();
}

/**
 * Represents a coordinate on the game board.
 * Provides information about occupancy and hit status and basic actions
 * (occupy, shoot) as well as adjacency checks.
 *
 * Implementations are expected to be immutable in coordinates but mutable
 * in occupancy/hit state.
 *
 * @author fba
 */
package iscteiul.ista.battleship;

/**
 * @author fba
 */
public interface IPosition {

    /**
     * Returns the row index of this position.
     *
     * @return row index
     */
    int getRow();

    /**
     * Returns the column index of this position.
     *
     * @return column index
     */
    int getColumn();

    /**
     * Equality is based on row and column coordinates only.
     *
     * @param other object to compare
     * @return true if coordinates are the same
     */
    boolean equals(Object other);

    /**
     * Checks whether this position is adjacent (including diagonals) to {@code other}.
     *
     * @param other position to compare
     * @return true if positions are at most 1 cell apart in both dimensions
     */
    boolean isAdjacentTo(IPosition other);

    /**
     * Mark this position as occupied by a ship.
     */
    void occupy();

    /**
     * Mark this position as having been shot (hit).
     */
    void shoot();

    /**
     * Indicates whether this position is occupied by a ship.
     *
     * @return true if occupied
     */
    boolean isOccupied();

    /**
     * Indicates whether this position has been shot.
     *
     * @return true if hit
     */
    boolean isHit();
}
